-- *************************************************************************************************
-- This script creates all of the database objects (tables, sequences, etc) for the database
-- *************************************************************************************************

BEGIN;

-- CREATE statements go here

CREATE SEQUENCE seq_student_id;
CREATE TABLE student_profile (

	student_id integer NOT NULL DEFAULT nextval('seq_student_id'),
	firstName VARCHAR(20),
	lastName VARCHAR(20),
	username VARCHAR(32) NOT NULL,
	cohort VARCHAR(25),
	courseLanguage VARCHAR(20),
	streetAddress VARCHAR(40),
	city VARCHAR(40),
	state VARCHAR(2),
	postCode integer,
	email VARCHAR(40),
	phoneNumber VARCHAR(20),
	summary VARCHAR(1000),
	careerExperience VARCHAR(50),
	interests VARCHAR(60),
	academicBackground VARCHAR(40),
	skills VARCHAR(50),

	CONSTRAINT pk_student_profile_username PRIMARY KEY (username)
);

CREATE SEQUENCE seq_employer_id;
CREATE TABLE employer (
	employer_id integer NOT NULL DEFAULT nextval('seq_employer_id'),
	username VARCHAR(32) NOT NULL,
	company_name VARCHAR(50),
	CONSTRAINT pk_employer_employer_id PRIMARY KEY (employer_id)
);

CREATE SEQUENCE seq_staff_id;
CREATE TABLE staff (
	staff_id integer NOT NULL DEFAULT nextval('seq_staff_id'),
	username VARCHAR(32) NOT NULL,
	CONSTRAINT pk_staff_staff_id PRIMARY KEY (staff_id)
);

CREATE TABLE app_user (
  username varchar(32) NOT NULL,
  password varchar(32) NOT NULL,
  user_type varchar(32) NOT NULL,
  salt varchar(256) NOT NULL,	
  CONSTRAINT pk_app_user_username PRIMARY KEY (username)
);

CREATE SEQUENCE seq_project_id;
CREATE TABLE project (
	project_id integer NOT NULL DEFAULT nextval('seq_project_id'),
	username varchar(32) NOT NULL,
	projectName varchar(50),
	description varchar(5000),
	projectType varchar(20),
	CONSTRAINT pk_project_project_id PRIMARY KEY (project_id),
	CONSTRAINT fk_project_student_profile FOREIGN KEY (username) REFERENCES student_profile (username)
);











COMMIT;